import React from 'react';

const pomodoroData = {
  pomodoroText: "Pomodoro",
  shortBreakText: "Short Break",
  longBreakText: "Long Break",
}

function Button(props) {
  return (
    <li>
      <button>{props.buttonText}</button>
    </li>
  );
}

class TimerOption extends React.Component {
  renderButton(buttonText) {
   return <Button buttonText={buttonText} />
  }
  render() {
    return (
      <div>
        <ul>
          {this.renderButton(this.props.pomodoroText)};
          {this.renderButton(this.props.shortBreakText)};
          {this.renderButton(this.props.longBreakText)};
        </ul>
      </div>
    );
  }

}

class PomodoroClock extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      pomodoroTime: 25, 
      shortBreak: 5,
      longBreak: 10,
      paused: true,
    };
    this.tick = this.tick.bind(this);
    this.startTimer = this.startTimer.bind(this);
    this.stopTimer = this.stopTimer.bind(this);
    this.reset = this.reset.bind(this);
	}

  tick = () => {
    if(!this.state.timer) {
      clearInterval( this.interval)
    } else {
      this.setState({ timer: this.state.timer - 1 });
    }
  }

	startTimer = () => {
		this.interval = setInterval(this.tick,1000);
    this.setState({ paused: false });
	}

  stopTimer = () => {
  	clearInterval( this.interval );
    this.setState({ paused: true });
	}

  reset = () => {
  	this.setState({ timer: `${this.state.startTime}`, paused: true });
    clearInterval( this.interval );
  }
  render() {
    return (
      <TimerOption />
    );
  }
}

export default PomodoroClock;
